// convertToDiscussion은 아고라 스테이츠 데이터를 DOM으로 바꿔줍니다.

const convertToDiscussion = (obj) => {
  const li = document.createElement("li"); // li 요소 생성
  li.className = "discussion__container"; // 클래스 이름 지정

  const avatarWrapper = document.createElement("div");
  avatarWrapper.className = "discussion__avatar--wrapper";
  const discussionContent = document.createElement("div");
  discussionContent.className = "discussion__content";
  const discussionAnswered = document.createElement("div");
  discussionAnswered.className = "discussion__answered";
  
  // 아바타 프로필 사진이 들어갈 img 요소 생성
  const avatarImg = document.createElement('img');
    avatarImg.className = "discussion__avatar--image";
    avatarImg.src = obj.avatarUrl;
    avatarImg.alt = 'avatar of ' + obj.author;
    avatarWrapper.append(avatarImg);
  
  // 디스커션 제목 생성
  const discussionTitle = document.createElement("h2");
    discussionTitle.className = "discussion__title";
    discussionContent.append(discussionTitle);
  const tittleLink = document.createElement("a");
    tittleLink.href = obj.url;
    tittleLink.textContent = obj.title;
    discussionTitle.append(tittleLink);

  // 디스커션 정보 생성
  const discussionInformation = document.createElement("div");
    discussionInformation.className = "discussion__information";
    discussionInformation.textContent = `${obj.author} / ${obj.createdAt}`;
    discussionContent.append(discussionInformation);
  
  // 답변 체크박스 생성
  const checkBox = document.createElement("p");
  checkBox.textContent = "☑";
  discussionAnswered.append(checkBox);

  // 위에서 생성한 <div>요소들을 li.discussion__container의 자식 요소로 추가
  li.append(avatarWrapper, discussionContent, discussionAnswered);

  return li;
};

// agoraStatesDiscussions 배열의 모든 데이터를 화면에 렌더링하는 함수입니다.
const render = (element) => {
  for (let i = 0; i < agoraStatesDiscussions.length; i += 1) {
    element.append(convertToDiscussion(agoraStatesDiscussions[i]));
  }
  return;
};

