const submitClick = (e) => {
  // Event 인터페이스의 preventDefault() 메서드는 어떤 이벤트를 명시적으로 처리하지 않은 경우, 해당 이벤트에 대한 사용자 에이전트의 기본 동작을 실행하지 않도록 지정합니다.
  e.preventDefault();
  data.author = userName.value;
  data.title = questionTitle.value;
  agoraStatesDiscussions.push(data);

  ul.prepend(
    convertToDiscussion(
      agoraStatesDiscussions[agoraStatesDiscussions.length - 1]
    )
  );
  console.log(agoraStatesDiscussions[agoraStatesDiscussions.length - 1]);

}

let submit = document.getElementById("submit");
submit.addEventListener("click", submitClick);



