const convertToDiscussion = (obj) => { // ì—¬ê¸°ì„œ ì˜ë¬¸ì´ ë“œëŠ”ê²Œ í•¨ìˆ˜ë°–ì—ì„œ ì„ ì–¸í•´ì„œ ì“°ë©´ ì™œì•ˆë ê¹Œ?

  // ìƒë‹¨ li
  const li = document.createElement("li"); 
    li.className = "discussion__container"; 
  
  //  ì•„ë°”íƒ€ ìš”ì†Œ
  const avatarWrapper = document.createElement("div");                 // parent
  const avatarImg = document.createElement('img');                     // children
    avatarWrapper.className = "discussion__avatar__wrapper";
    avatarImg.className =  'discussion__avatar--image';
    avatarImg.src = obj.avatarUrl;                                     // ì•„ë°”íƒ€ ì´ë¯¸ì§€
    avatarImg.alt = 'avatar of ' + obj.author;                         // ì•„ë°”íƒ€ ì„¤ëª… (ë“£ê¸°ìš©)
  
  // ì œëª© , ë‚ ì§œ ìš”ì†Œ
  const discussionContent = document.createElement("div");             // ë¶€ëª¨
  const discussionTitlle = document.createElement('h2');               // ì œëª©ìœ¼ë¡œì“¸ h2 ìƒì„±
  const titleAngcer = document.createElement('a');                     // h2 ì œëª©ì— ë§í¬ë„£ê¸°ìœ„í•œ ìš”ì†Œ 
  const discussionInformation = document.createElement('div')          // ë‚ ì§œìš”ì†Œ
  const createDate = new Date(obj.createdAt).toLocaleTimeString();     // ë‚ ì§œê°’ ë°›ì•„ì„œ ì‹œê°„ë§Œ ì¶œë ¥
    discussionContent.className = "discussion__content"; 
    discussionTitlle.className = "discussion__title";
    discussionInformation.className = 'discussion__information' ;
    discussionInformation.textContent = createDate;                    
    titleAngcer.href = obj.url;                                        // ì œëª©ê´€ë ¨ ë§í¬
    titleAngcer.textContent = obj.title;                               // ì œëª©ë‚´ìš©
  
  // ì²´í¬ë°•ìŠ¤
  const discussionAnswered = document.createElement("div");            // parent
  const titleParagraph  = document.createElement('p');                 // ì²´í¬ë°•ìŠ¤ë¡œ ì‚¬ìš©í•  píƒœê·¸ ìš”ì†Œ
    discussionAnswered.className = "discussion__answered";          
    titleParagraph.textContent = obj.answer = null ? 'â˜‘' : 'ğŸ…‡';       // píƒœê·¸ ì¡°ê±´ë¬¸
  
    
  // ìµœì¢… ë Œë”ë§
    avatarWrapper.append(avatarImg);                                 
    discussionAnswered.append(titleParagraph);                         
    discussionTitlle.append(titleAngcer);                              
    discussionContent.append(discussionTitlle,discussionInformation);  
    li.append(avatarWrapper, discussionContent, discussionAnswered);  
  
  return li;
      
};

// agoraStatesDiscussions ë°°ì—´ì˜ ëª¨ë“  ë°ì´í„°ë¥¼ í™”ë©´ì— ë Œë”ë§í•˜ëŠ” í•¨ìˆ˜ì…ë‹ˆë‹¤.
const render = (element) => {
  for (let i = 0; i < agoraStatesDiscussions.length; i += 1) {
    element.append(convertToDiscussion(agoraStatesDiscussions[i]));
  }
  return;
};

